<?phpfunction createproject($project_name,$crop_name,$file,$array_name) {	$project_dir=	strtolower(preg_replace('([^A-Za-z0-9])', '', $project_name));	$path		=	"projects/" . $project_dir;	$path		=	dirname(__DIR__) . "/" . $path;		mkdir($path,0777);	    $file_title 	= 	 basename($_FILES[$file]['name']);		move_uploaded_file($_FILES[$file]['tmp_name'], $path ."/". $file_title); 		$conn	= start_conection();		$sql 	= "SELECT COUNT(*) AS `count` FROM `project` WHERE `name` = '" . $project_name . "';";	if ($rs = mysqli_query($conn, $sql)) {	$result = mysqli_fetch_assoc($rs);	if ($result["count"]){		$status = "proyect_exist";		mysqli_close($conn);		return $status;		} }		$sql 	= "INSERT INTO `project` (`name`, `dir`, `crop`, `file`, `array`, `owner`) VALUES ('" . $project_name . "', '" . $project_dir . "', '" . $crop_name . "', '" . $file_title . "', '" . $array_name . "', '" . $_SESSION["ClonDiagUser"] . "');";	$rs 	= mysqli_query($conn, $sql);		$sql 	= "SELECT `id` FROM `project` WHERE `name` = '" . $project_name . "';";	$rs 	= mysqli_query($conn, $sql);	$result = mysqli_fetch_assoc($rs);	$id_pj	= $result["id"];		$sql 	= "SELECT `id` FROM `user` WHERE `username` = '" . $_SESSION["ClonDiagUser"] . "';";	$rs 	= mysqli_query($conn, $sql);	$result = mysqli_fetch_assoc($rs);	$id_us	= $result["id"];		$sql 	= "INSERT INTO `permission_main` (`project`, `user`) VALUES ('" . $id_pj . "', '" . $id_us . "');";	$rs 	= mysqli_query($conn, $sql);			if ($rs){		$status	= "proyect_ok";		}		mysqli_close($conn);			return $status;	}?>