<?phpfunction permissionproject($project_name,$username) {	$conn	= start_conection();	$status = "perm_proj_error";			$sql 	= "INSERT INTO `permission_main` (`project`, `user`) VALUES ('" . $project_name . "', '" . $username . "');";	$rs 	= mysqli_query($conn, $sql);			if ($rs){			$status	= "proyect_ok";		}		mysqli_close($conn);			return $status;	}function verifypermissionproject($project_name,$username) {	$conn	= start_conection();	$status = "no_exist";			$sql 	= "SELECT * FROM `permission_main` WHERE `project`='" . $project_name . "' AND `user`='" . $username . "';";	$rs 	= mysqli_query($conn, $sql);	if (mysqli_num_rows($rs) > 0) {			$status	= "exist";	}		mysqli_close($conn);			return $status;	}?>