<?phpfunction permissiontest($test_name,$username) {	$conn	= start_conection();	$status = "perm_test_error";			$sql 	= "INSERT INTO `permission_test` (`test`, `user`) VALUES ('" . $test_name . "', '" . $username . "');";	$rs 	= mysqli_query($conn, $sql);			if ($rs){		$status	= "perm_test_ok";		}		mysqli_close($conn);			return $status;	}function verifypermissiontest($test_name,$username) {	$conn	= start_conection();	$status = "no_exist";			$sql 	= "SELECT * FROM `permission_test` WHERE `test`='" . $test_name . "' AND `user`='" . $username . "';";	$rs 	= mysqli_query($conn, $sql);	if (mysqli_num_rows($rs) > 0) {			$status	= "exist";	}		mysqli_close($conn);			return $status;	}?>